public static class Razsiritve {
    // Razširitvena metoda za string
    public static int SteviloBesed(this string str) {
        if (string.IsNullOrWhiteSpace(str)) {
            return 0;
        }
        return str.Split(new char[] { ' ', '\t', '\n' }, StringSplitOptions.RemoveEmptyEntries).Length;
    }

    // Razširitvena metoda za List<T>
    public static T NakljucniElement<T>(this List<T> seznam) {
        if (seznam == null || seznam.Count == 0) {
            throw new ArgumentException("Seznam je prazen");
        }
        Random rnd = new Random();
        return seznam[rnd.Next(seznam.Count)];
    }
}

